import React from "react";
import { useSelector } from "react-redux";

import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";

import rightArror from "../../images/arrow-right.svg";

function BlogArticle(props) {
  const blogs = props.blogs;
  return (
    <div className="BlogArticle">
      <Row>
        {blogs?.map((blog, index) => (
          <Col key={index} xs={12} md={6} lg={4} className="mt-5">
            <Card style={{ border: "none" }}>
              <div className="h-[240px] w-[100%] max-w-[100%]">
                <Card.Img variant="top" src={blog.selectedFile} />
              </div>
              <p className=" mt-[16px] m-[0] ">{blog.category}</p>
              <Card.Title className="card_title smallFont my-3">
                {blog.title.substr(0, 25)}
                {blog.title.length > 25 && <span>...</span>}
              </Card.Title>
              <Card.Text className="card_text extraSmallFont">
                {blog.description.substr(0, 200)}
              </Card.Text>
              <p className="extraSmallFont blog_date">
                July 14, <span>{blog.creator}</span>
              </p>
              <div className="section_heading">
                <a className="extraSmallFont" href={blog.link}>
                  Read More
                </a>
                <span className="right_arrow">
                  <img
                    className="img-fluid"
                    src={rightArror}
                    alt="rightArror"
                  />
                </span>
              </div>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}

export default BlogArticle;
